package grupoa;

public class SumaNumeros {
	//el num es el valor que ponemos
	public int sumaNumeros(int num) {
		int suma = 0;
		//todo funciona de todas formas
		for (int i = 1; i < num; i++) {
			if(i%2==0) {
				suma += i;
			}
		}
		return suma;
	}
}

//si el valor de num es menor de 1 o 1, no funciona el for y suma es 0
//si el valor es mayor de 1, entonces funciona hasta que se termine el for


